import React, { useState } from 'react';

function SetSelection(props) {
  const [selectedSet, setSelectedSet] = useState('');

  const handleChange = (event) => {
    setSelectedSet(event.target.value);
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    props.onStartDraft(selectedSet);
  }

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Choose a set to draft:
        <select value={selectedSet} onChange={handleChange}>
          <option value="">Select a set</option>
          <option value="THB">Theros Beyond Death</option>
          <option value="IKO">Ikoria: Lair of Behemoths</option>
          <option value="M21">Core Set 2021</option>
        </select>
      </label>
      <button type="submit">Start Draft</button>
    </form>
  );
}

export default SetSelection;
