import React, { useState, useEffect } from 'react';
import getCardImage from './getCardImage';

function BoosterPack({ set, onComplete }) {
  const [cards, setCards] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [progress, setProgress] = useState(0);
  var steps = 0;
  
  useEffect(() => {
    async function fetchCards() {
        if (!set) return; // Return early if set is undefined
        setIsLoading(true);
        try {
          const basicLandResponse = await fetch(`https://api.scryfall.com/cards/random?q=set:${set} is:booster  t:basic unique:prints -stamp:arena`);
          const { name } = await basicLandResponse.json();
          const basicLand = await getCardImage(name, 'large', set);
          steps++;
          setProgress(steps / 15 * 100);
    
          const rareMythicResponse = await fetch(`https://api.scryfall.com/cards/random?q=set:${set} is:booster  (rarity:rare OR rarity:mythic) -stamp:arena`);
          const { name: rareMythicName } = await rareMythicResponse.json();
          const rareMythic = await getCardImage(rareMythicName, 'large', set);
          steps++;
          setProgress(steps / 15 * 100);
    
          const uncommons = [];
          for (let i = 0; i < 3; i++) {
            const response = await fetch(`https://api.scryfall.com/cards/random?q=set:${set} is:booster  rarity:uncommon -stamp:arena`);
            const { name } = await response.json();
            steps++;
            setProgress(steps / 15 * 100);
            uncommons.push(await getCardImage(name, 'large', set));
          }
    
          const commons = [];
          for (let i = 0; i < 10; i++) {
            const response = await fetch(`https://api.scryfall.com/cards/random?q=set:${set} is:booster  rarity:common -stamp:arena`);
            const { name } = await response.json();
            steps++;
            setProgress(steps / 15 * 100);
            commons.push(await getCardImage(name, 'large', set));
          }
    
          const foilChance = Math.random();
          let foilCard;
          if (foilChance < 0.33) {
            const response = await fetch(`https://api.scryfall.com/cards/random?q=set:${set} is:booster -stamp:arena`);
            const { name } = await response.json();
            foilCard = await getCardImage(name, 'large', set);
          }
    
          const cardImages = [rareMythic, ...uncommons, ...commons, basicLand];
          const randomIndex = Math.floor(Math.random() * cardImages.length);
          cardImages[randomIndex] = foilCard || cardImages[randomIndex];
    
          setCards(cardImages);
        } catch (error) {
          console.error(error);
        }
        setIsLoading(false);
    }
    
    fetchCards();
  }, [set]);
}

export default BoosterPack;
