import React, { useState } from 'react';

function SetSelection(props) {
  const [selectedSet, setSelectedSet] = useState('');

  const sets = ['ONE', 'BRO', 'DMU', 'SNC', 'NEO', 'LEA'];

  const handleChange = (event) => {
    setSelectedSet(event.target.value);
  }

  const handleSubmit = () => {
    if (selectedSet) {
      props.onStartDraft(selectedSet);
    } else {
      alert('Please select a set');
    }
  }

  const handleSealed = () => {
    if (selectedSet) {
      props.onStartSealed(selectedSet);
    } else {
      alert('Please select a set');
    }
  }
  
  
  return (
    <div className="Buttons">
      {/*<label>
        Select a Set:
        <select value={selectedSet} onChange={handleChange}>
          <option value="">--Select a set--</option>
          {sets.map((set, index) => (
            <option key={index} value={set}>{set}</option>
          ))}
        </select>
          </label> */}
      <button type="submit" className="mainButtons" onClick={handleSubmit}>Create a Drafting Lobby</button>
      <button type="submit" className="mainButtons" onClick={handleSubmit}>Join a Drafting Lobby</button>
      <button type="submit" className="mainButtons" onClick={handleSubmit}>Pack Opening Simulator</button>
      <button type="submit" className="mainButtons" onClick={handleSubmit}>Settings</button>
    </div>
  );
  
}

export default SetSelection;
