import './App.css';
import SetSelection from './components/SetSelection';
import getCardImage from './components/getCardImage';
import BoosterPack from './components/BoosterPack';
import React, { useState, useEffect } from 'react';

function App() {
  const [cards, setCards] = useState([]);
  const [set, setSet] = useState('');

  function onStartDraft(setCode) {
    setSet(setCode);
  }

  useEffect(() => {
    async function fetchCards() {
      if (set) {
        try {
          const basicLandResponse = await fetch(`https://api.scryfall.com/cards/random?q=set:${set} t:basic unique:prints`);
          const { name } = await basicLandResponse.json();
          const basicLand = await getCardImage(name, 'large', set);

          const rareMythicResponse = await fetch(`https://api.scryfall.com/cards/random?q=set:${set} (rarity:rare OR rarity:mythic)`);
          const { name: rareMythicName } = await rareMythicResponse.json();
          const rareMythic = await getCardImage(rareMythicName, 'large', set);

          const uncommons = await Promise.all(Array.from({ length: 3 }, async () => {
            const response = await fetch(`https://api.scryfall.com/cards/random?q=set:${set} rarity:uncommon -t:double-faced`);
            const { name } = await response.json();
            return getCardImage(name, 'large', set);
          }));

          const commons = await Promise.all(Array.from({ length: 10 }, async () => {
            const response = await fetch(`https://api.scryfall.com/cards/random?q=set:${set} rarity:common -t:double-faced`);
            const { name } = await response.json();
            return getCardImage(name, 'large', set);
          }));

          const foilChance = Math.random();
          let foilCard;
          if (foilChance < 0.33) {
            const response = await fetch(`https://api.scryfall.com/cards/random?q=set:${set} is:booster -t:double-faced`);
            const { name } = await response.json();
            foilCard = await getCardImage(name, 'large', set);
          }

          const cardImages = [rareMythic, ...uncommons, ...commons, basicLand];
          const randomIndex = Math.floor(Math.random() * cardImages.length);
          cardImages[randomIndex] = foilCard || cardImages[randomIndex];

          setCards(cardImages);
        } catch (error) {
          console.error(error);
        }
      }
    }
    fetchCards();
  }, [set]);


  return (
    <div className="App">
      <div className='cardGrid'>
        <h1>DraftStation</h1>
        <h4>Magic the Gathering Drafting Simulator</h4>
        <p>Welcome to the DraftStation! Create a drafting lobby or join one!</p>
        <SetSelection onStartDraft={onStartDraft} />

        {cards.length > 0 && (
          <div>
            <BoosterPack cards={cards} />
            <div className="card-grid">
              {cards.map((card, index) => (
                <img key={index} src={card} alt={`Card ${index + 1}`} className="magicCard"/>
              ))}
            </div>
          </div>
        )}
      </div>
      <footer className="footer">
        <div className="container">
          <span className="text-muted">
            something something legal and ownership plus some links
          </span>
        </div>
      </footer>
    </div>
  );
}

export default App;
