import './App.css';
import SetSelection from './components/SetSelection';
import getCardImage from './components/getCardImage';
import BoosterPack from './components/BoosterPack';
import React, { useState, useEffect } from 'react';

function App() {
  const [set, setSet] = useState('');
  const [cards, setCards] = useState([]);
  const [frozen, setFrozen] = useState(false);

  useEffect(() => {
    if (set) {
      setCards([]);
      setFrozen(false);
    }
  }, [set]);

  function generateSet() {
    const sets = ['ONE'];
    return sets[Math.floor(Math.random() * sets.length)];
  }

  function handleGenerateSet() {
    setSet(generateSet());
  }

  function handleBoosterPackComplete(cardImages) {
    setCards(cardImages);
    setFrozen(true);
  }

  function handleStartDraft(setCode) {
    console.log(`Starting draft for set ${setCode}`);
  }

  return (
    <div className="App">
      <h1>DraftStation</h1>
      <h4>Magic the Gathering Drafting Simulator</h4>
      <p>Welcome to the DraftStation! Create a drafting lobby or join one!</p>
      <SetSelection onStartDraft={handleStartDraft} />
      {set && (
        <div>
          <button onClick={handleGenerateSet}>Generate New Set</button>
          <BoosterPack set={set} onComplete={handleBoosterPackComplete} disabled={frozen} />
        </div>
      )}
      <div className="card-grid">
        {cards.map((card, index) => (
          <img key={index} src={card} alt={`Card ${index + 1}`} />
        ))}
      </div>
      <footer className="footer">
        <div className="container">
          <span className="text-muted">something something legal and ownership plus some links</span>
        </div>
      </footer>
    </div>
  );
}

export default App;
