import './App.css';
import SetSelection from './components/SetSelection';
import getCardImage from './components/getCardImage';
import BoosterPack from './components/BoosterPack';
import React, { useState, useEffect } from 'react';

function App() {
  const [cards, setCards] = useState([]);
  const [set, setSet] = useState('');

  function onStartDraft(setCode) {
    setSet(setCode);
    setCards(getCardImage(setCode));
  }

  return (
    <div className="App">
      <h1>DraftStation</h1>
      <h4>Magic the Gathering Drafting Simulator</h4>
      <p>Welcome to the DraftStation! Create a drafting lobby or join one!</p>
      <SetSelection onStartDraft={onStartDraft} />

      {set && (
        <div>
          <BoosterPack set={set} onComplete={setSet} />
          <div className="card-grid">
            {cards.length > 0 && cards.map((card, index) => (
              <img key={index} src={card} alt={`Card ${index + 1}`} />
            ))}
          </div>
        </div>
      )}

      <footer className="footer">
        <div className="container">
          <span className="text-muted">
            something something legal and ownership plus some links
          </span>
        </div>
      </footer>
    </div>
  );
}

export default App;
