import './App.css';
import SetSelection from './components/SetSelection';
import getCardImage  from './components/getCardImage';
import BoosterPack from './components/BoosterPack';
import React, { useState, useEffect } from 'react';

function DraftingArea({set, onComplete}) {
  const [cards, setCards] = useState([]);

  function onSetChange(cardImages) {
    setCards(cardImages);
    onComplete();
  }

  return (
    <>
      <BoosterPack set={set} onComplete={onSetChange} />
      <div className="card-grid">
        {cards.map((card, index) => (
          <img key={index} src={card} alt={`Card ${index + 1}`} />
        ))}
      </div>
    </>
  );
}

function App() {
  const [set, setSet] = useState('');
  const [isDrafting, setIsDrafting] = useState(false);

  function generateSet() {
    const sets = ['ONE'];
    return sets[Math.floor(Math.random() * sets.length)];
  }

  function onStartDraft(setCode) {
    console.log(`Starting draft for set ${setCode}`);
    setSet(setCode);
    setIsDrafting(true);
  }

  return (
    <div className="App">
      <h1>DraftStation</h1>
      <h4>Magic the Gathering Drafting Simulator</h4>
      <p>Welcome to the DraftStation! Create a drafting lobby or join one!</p>
      <SetSelection onStartDraft={onStartDraft} />

      {isDrafting && <DraftingArea set={set} onComplete={() => setIsDrafting(false)} />}

      <footer className="footer">
        <div className="container">
          <span className="text-muted">something something legal and ownership plus some links</span>
        </div>
      </footer>
    </div>
  );
}

export default App;
