import './App.css';
import SetSelection from './components/SetSelection';
import getCardImage from './components/getCardImage';
import BoosterPack from './components/BoosterPack';
import React, { useState, useEffect } from 'react';

function App() {
  const [cards, setCards] = useState([]);
  const [set, setSet] = useState('');

  function onStartDraft(setCode) {
    setSet(setCode);
  }

  useEffect(() => {
    async function fetchCards() {
      if (set) {
        try {
          const response = await fetch(`https://api.scryfall.com/cards/random?q=set:${set} (rarity:rare OR rarity:mythic)`);
          const { name } = await response.json();
          const rareMythic = await getCardImage(name, 'small');

          const basicLand = await getCardImage(`Basic Land - ${set}`, 'small');

          const uncommons = await Promise.all(Array.from({ length: 3 }, async () => {
            const response = await fetch(`https://api.scryfall.com/cards/random?q=set:${set} rarity:uncommon`);
            const { name } = await response.json();
            return getCardImage(name, 'small');
          }));
          const commons = await Promise.all(Array.from({ length: 10 }, async () => {
            const response = await fetch(`https://api.scryfall.com/cards/random?q=set:${set} rarity:common`);
            const { name } = await response.json();
            return getCardImage(name, 'small');
          }));
          const cardImages = [rareMythic, ...uncommons, , ...commons];
          setCards(cardImages);
        } catch (error) {
          console.error(error);
        }
      }
    }
    fetchCards();
  }, [set]);

  return (
    <div className="App">
      <h1>DraftStation</h1>
      <h4>Magic the Gathering Drafting Simulator</h4>
      <p>Welcome to the DraftStation! Create a drafting lobby or join one!</p>
      <SetSelection onStartDraft={onStartDraft} />

      {cards.length > 0 && (
        <div>
          <BoosterPack cards={cards} />
          <div className="card-grid">
            {cards.map((card, index) => (
              <img key={index} src={card} alt={`Card ${index + 1}`} className="magicCard"/>
            ))}
          </div>
        </div>
      )}

      <footer className="footer">
        <div className="container">
          <span className="text-muted">
            something something legal and ownership plus some links
          </span>
        </div>
      </footer>
    </div>
  );
}

export default App;
