import './App.css';
import SetSelection from './components/SetSelection';
import getCardImage from './components/getCardImage';
import BoosterPack from './components/BoosterPack';
import React, { useState } from 'react';

function App() {
  const [set, setSet] = useState('');
  const [cards, setCards] = useState([]);

  function onSetChange(cardImages) {
    setCards(cardImages);
  }

  function handleStartDraft(setCode) {
    setSet(setCode);
  }

  function handleGenerateSet() {
    const sets = ['ONE', 'TWO', 'THREE'];
    setSet(sets[Math.floor(Math.random() * sets.length)]);
  }

  return (
    <div className="App">
      <h1>DraftStation</h1>
      <h4>Magic the Gathering Drafting Simulator</h4>
      <p>Welcome to the DraftStation! Create a drafting lobby or join one!</p>
      <SetSelection onStartDraft={handleStartDraft} onGenerateSet={handleGenerateSet} />

      {set && <BoosterPack set={set} onComplete={onSetChange} />}
      <div className="card-grid">
        {cards.map((card, index) => (
          <img key={index} src={card} alt={`Card ${index + 1}`} />
        ))}
      </div>

      <footer className="footer">
        <div className="container">
          <span className="text-muted">
            something something legal and ownership plus some links
          </span>
        </div>
      </footer>
    </div>
  );
}

export default App;
