import React from "react";
import "./MagicCard.css";

function MagicCard(props) {
  const { src, alt } = props;

  function handleMouseMove(e) {
    const card = e.currentTarget;
    const halfCardWidth = card.offsetWidth / 2;

    const cardX = card.offsetLeft + halfCardWidth;
    const mouseX = e.clientX;

    const rotateY = (cardX - mouseX) / 10;
    card.style.transform = `rotateY(${rotateY}deg) scale(1.25) translateZ(40px)`;
  }

  function handleMouseLeave(e) {
    const card = e.currentTarget;
    card.style.transform = '';
  }

  return (
    <div className="magicCard" onMouseMove={handleMouseMove} onMouseLeave={handleMouseLeave}>
      <img src={src} alt={alt} />
    </div>
  );
}

export default MagicCard;
