import './App.css';
import SetSelection from './components/SetSelection';
import getCardImage  from './components/getCardImage';
import BoosterPack  from './components/BoosterPack';

import axios from 'axios';
import React, { useState, useEffect } from 'react';



function App() {
  const [cardName, setCardName] = useState('');
  const [cardImage, setCardImage] = useState('');

  const handleInputChange = (event) => {
    setCardName(event.target.value, 'normal');
  }

  const handleFormSubmit = async (event) => {
    event.preventDefault();

    const imgUrl = await getCardImage(cardName);
    setCardImage(imgUrl);
  }

  const [cardImages, setCardImages] = useState([]);

  function handleBoosterPackComplete(images) {
    setCardImages(images);
  }

  return (
    <div className="App">
      <h1>DraftStation</h1>
      <h4>Magic the Gathering Drafting Simulator</h4>
      <p>Welcome to the DraftStation! Create a drafting lobby or join one!</p>
      <SetSelection onStartDraft={handleStartDraft} />

      <BoosterPack set="Kaldheim" onComplete={handleBoosterPackComplete} />
      {cardImages.length > 0 ? (
        <div>
          <h2>Your Generated Booster Pack</h2>
          <div>
            {cardImages.map((card, index) => (
              <img key={index} src={card} alt={`Card ${index}`} />
            ))}
          </div>
        </div>
      ) : (
        <p>Loading booster pack...</p>
      )}

      {cardImage && <img src={cardImage} alt={cardName} />}

      <footer className="footer">

      <div className="container">
        <span className="text-muted">something something legal and ownership plus some links</span>
      </div>
    </footer>
    </div>
  );
}

const handleStartDraft = (setCode) => {
  console.log(`Starting draft for set ${setCode}`);
}


export default App;
