import React, { useState } from 'react';

function SetSelection(props) {
  const [selectedSet, setSelectedSet] = useState('');
  const [isShowingClose, setisShowingClose] = useState(false);

  const sets = ['ONE', 'BRO', 'DMU', 'SNC', 'NEO', 'LEA'];

  const handleChange = (event) => {
    setSelectedSet(event.target.value);
  }

  const handleSubmit = () => {
    if (selectedSet) {
      props.onStartOpen(selectedSet);
      setisShowingClose(true);
    } else {
      alert('Please select a set');
    }
  }

  const handleClose = () => {
    if (selectedSet) {
      props.onClosePack();
      setisShowingClose(false);
    } else {
      alert('Please select a set');
    }
  }

  return (
    <div className="Buttons">
      {
        <label>
          Select a Set:
            <select value={selectedSet} onChange={handleChange}>
              <option value="">--Select a set--</option>
              {sets.map((set, index) => (
                <option key={index} value={set}>{set}</option>
              ))}
            </select>
        </label>
      }

      {
        isShowingClose ? (<button type="submit" className="mainButtons" onClick={handleSubmit}>Open a Pack!</button>) :
        (<button type="submit" className="mainButtons" onClick={handleClose}>Clear</button>) 
      }
    </div>
  );
  
}

export default SetSelection;
