import React, { useState, useEffect } from 'react';

function SetSelection(props) {
  const [selectedSet, setSelectedSet] = useState('');
  const [isShowingClose, setisShowingClose] = useState(false);
  const [setCodes, setSetCodes] = useState([]);

  useEffect(() => {
    async function fetchSetCodes() {
      try {
        const response = await axios.get('https://api.scryfall.com/sets', {
          params: {
            q: 'legal:modern',
            format: 'json',
            'order': 'released_at',
            'direction': 'desc',
            'set_type': 'core|expansion'
          }
        });

        const sets = response.data.data.map((set) => [set.code, set.name.toUpperCase()]);

        setSetCodes(sets);
      } catch (error) {
        console.error(error);
      }
    }

    fetchSetCodes();
  }, []);


  const handleChange = (event) => {
    setSelectedSet(event.target.value);
  }

  const handleSubmit = () => {
    if (selectedSet) {
      props.onStartOpen(selectedSet);
      setisShowingClose(true);
    } else {
      alert('Please select a set');
    }
  }

  const handleClose = () => {
    if (selectedSet) {
      props.onClosePack();
      setisShowingClose(false);
    } else {
      alert('Please select a set');
    }
  }

  return (
    <div className="Buttons">
      {
        isShowingClose ? (<button type="submit" className="mainButtons" onClick={handleClose}>Clear</button>) :
        (<button type="submit" className="mainButtons" onClick={handleSubmit}>Open a Pack!</button>) 
      }
      {
        <label>
          Select a Set:
            <select value={selectedSet} onChange={handleChange}>
              <option value="">--Select a set--</option>
              {setCodes.map((set, index) => (
                <option key={index} value={set}>{set}</option>
              ))}
            </select>
        </label>
      }
    </div>
  );
  
}

export default SetSelection;
