import './App.css';
import SetSelection from './components/SetSelection';
import getCardImage  from './components/getCardImage';

import axios from 'axios';
import React, { useState, useEffect } from 'react';



function App() {
  const [cardImage, setCardImage] = useState('');

  useEffect(() => {
    const getCardImage = async () => {
      try {
        const response = await axios.get('https://api.scryfall.com/cards/named?fuzzy=Revitalize', {
          params: {
            format: 'image',
            version: 'normal'
          },
          responseType: 'arraybuffer'
        });

        const blob = new Blob([response.data], {type: 'image/jpeg'});
        const imgUrl = URL.createObjectURL(blob);

        setCardImage(imgUrl);
      } catch (error) {
        console.error(error);
      }
    }

    getCardImage();
  }, []);

  return (
    <div className="App">
      <h1>DraftStation</h1>
      <h4>Magic the Gathering Drafting Simulator</h4>
      <p>Welcome to the DraftStation! Create a drafting lobby or join one!</p>
      <SetSelection onStartDraft={handleStartDraft} />
      <img src={cardImage} alt="Revitalize" />
      <footer className="footer">

      <div className="container">
        <span className="text-muted">something something legal and ownership plus some links</span>
      </div>
    </footer>
    </div>
  );
}

const handleStartDraft = (setCode) => {
  console.log(`Starting draft for set ${setCode}`);
}


export default App;
