import './App.css';
import SetSelection from './components/SetSelection';
import getCardImage from './components/getCardImage';
import React, { useState, useEffect } from 'react';

function App() {
  const [cards, setCards] = useState([]);
  const [set, setSet] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isGeneratingPack, setIsGeneratingPack] = useState(false);

  async function fetchCards(set) {
    console.log("Fetching cards...");
    if (!set) return; // Return early if set is undefined
    try {
      const basicLandResponse = await fetch(`https://api.scryfall.com/cards/random?q=set:${set} is:booster  t:basic unique:prints -stamp:arena`);
      const { name } = await basicLandResponse.json();
      const basicLand = await getCardImage(name, 'large', set);

      const rareMythicResponse = await fetch(`https://api.scryfall.com/cards/random?q=set:${set} is:booster  (rarity:rare OR rarity:mythic) -stamp:arena`);
      const { name: rareMythicName } = await rareMythicResponse.json();
      const rareMythic = await getCardImage(rareMythicName, 'large', set);

      const uncommons = [];
      for (let i = 0; i < 3; i++) {
        const response = await fetch(`https://api.scryfall.com/cards/random?q=set:${set} is:booster  rarity:uncommon -stamp:arena`);
        const { name } = await response.json();
        uncommons.push(await getCardImage(name, 'large', set));
      }

      const commons = [];
      for (let i = 0; i < 10; i++) {
        const response = await fetch(`https://api.scryfall.com/cards/random?q=set:${set} is:booster  rarity:common -stamp:arena`);
        const { name } = await response.json();
        commons.push(await getCardImage(name, 'large', set));
      }

      const foilChance = Math.random();
      let foilCard;
      if (foilChance < 0.33) {
        const response = await fetch(`https://api.scryfall.com/cards/random?q=set:${set} is:booster -stamp:arena`);
        const { name } = await response.json();
        foilCard = await getCardImage(name, 'large', set);
      }

      const cardImages = [rareMythic, ...uncommons, ...commons, basicLand];
      const randomIndex = Math.floor(Math.random() * cardImages.length);
      cardImages[randomIndex] = foilCard || cardImages[randomIndex];

      console.log(cardImages.length);

      return cardImages;
    } catch (error) {
      console.error(error);
      return [];
    }
  }



  function onStartDraft(setCode) {
    setSet(setCode);
    fetchCards(setCode);
    setIsGeneratingPack(true);
    console.log("Selected set code %s", setCode)
  }

  return (
    <div className="App">
      <div className='cardGrid'>
        <h1>DraftStation</h1>
        <h4>Magic the Gathering Drafting Simulator</h4>
        <p>Welcome to the DraftStation! Create a drafting lobby or join one! Number of Cards: {cards.length}</p>
        <SetSelection onStartDraft={onStartDraft} setIsGeneratingPack={setIsGeneratingPack} />
        <div className="card-grid">
          {cards.map((card, index) => (
            <img key={index} src={card} alt={`Card ${index + 1}`} className="magicCard" />
          ))}
        </div>
      </div>
      <footer className="footer">
        <div className="container">
          <span className="text-muted">
            something something legal and ownership plus some links
          </span>
        </div>
      </footer>
    </div>
  );
}

export default App;
