import React from "react";
import "./MagicCard.css";

function MagicCardLoading(props) {
  const { src, alt, isFoil } = props;
  const [rotateX, setRotateX] = useState(0);
  const [rotateY, setRotateY] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      // Calculate new rotation values
      const newRotateX = rotateX + Math.random() * 2 - 1;
      const newRotateY = rotateY + Math.random() * 2 - 1;

      // Update state
      setRotateX(newRotateX);
      setRotateY(newRotateY);
    }, 50);

    return () => clearInterval(interval);
  }, [rotateX, rotateY]);


  function handleMouseMove(e) {
    const card = e.currentTarget;
    const halfCardWidth = card.offsetWidth / 2;
    const halfCardHeight = card.offsetHeight / 2;
    const cardRect = card.getBoundingClientRect();
    const cardX = cardRect.left + halfCardWidth;
    const cardY = cardRect.top + halfCardHeight;
    const mouseX = e.clientX;
    const mouseY = e.clientY;
    const rotateX = -1*(cardY - mouseY) / 25;
    const rotateY = (cardX - mouseX) / 20;
    const rotateZ = 0;
    const perspective = '1000px';
  }
  
  function handleMouseLeave(e) {
    const card = e.currentTarget;
    card.style.transform = 'scale(1)';
  }

  return (
    <div className={`magicCardLoading`} 
    style={{
      transform: `perspective(1000px) rotate3d(0, 1, 0, ${rotateY}deg) rotate3d(1, 0, 0, ${rotateX}deg) rotate3d(0, 0, 1, 0deg) scale(1.25)`,
    }}
    >
      <img src={src} alt={alt} />
    </div>
  );
}


export default MagicCardLoading;
